/************************************************************************
 * Country Channel Valid
 *  Input:  CountryCode, ChannelNum
 *          ChanneIndex is defined as VT3253 MAC channel:
 *              1   = 2.4G channel 1
 *              2   = 2.4G channel 2
 *              ...
 *              14  = 2.4G channel 14
 *              15  = 4.9G channel 183
 *              16  = 4.9G channel 184
 *              .....
 *  Output: TRUE if the specified 5GHz band is allowed to be used.
            False otherwise.
// 4.9G => Ch 183, 184, 185, 187, 188, 189, 192, 196 (Value:15 ~ 22)

// 5G => Ch 7, 8, 9, 11, 12, 16, 34, 36, 38, 40, 42, 44, 46, 48, 52, 56, 60, 64,
// 100, 104, 108, 112, 116, 120, 124, 128, 132, 136, 140, 149, 153, 157, 161, 165 (Value 23 ~ 56)
 ************************************************************************/
BOOL
ChannelValid(unsigned int CountryCode, unsigned int ChannelIndex)
{
    BOOL    bValid;

    bValid = FALSE;
    /*
     * If Channel Index is invalid, return invalid
     */
    if ((ChannelIndex > CB_MAX_CHANNEL) ||
        (ChannelIndex == 0))
    {
        bValid = FALSE;
        goto exit;
    }

    bValid = sChannelTbl[ChannelIndex].bValid;

exit:
    return (bValid);

} /* end ChannelValid */

/************************************************************************
 * CHvChannelGetList
 * Get Available Channel List for a given country
 * Input:
 *      CountryCode     =   The country code defined in country.h
 * Output:
 *      ChannelBitMask  =   (QWORD *) correspondent bit mask
 *                          of available channels
 *                          0x0000000000000001 means channel 1 is supported
 *                          0x0000000000000003 means channel 1,2 are supported
 *                          0x000000000000000F means channel 1,2,..15 are supported
 ************************************************************************/
BOOL
CHvChannelGetList (
      unsigned int       uCountryCodeIdx,
     PBYTE      pbyChannelTable
    )
{
    if (uCountryCodeIdx >= CCODE_MAX) {
        return (FALSE);
    }
    memcpy(pbyChannelTable, ChannelRuleTab[uCountryCodeIdx].bChannelIdxList, CB_MAX_CHANNEL);
    return (TRUE);
}


void CHvInitChannelTable(void *pDeviceHandler)
{
    PSDevice    pDevice = (PSDevice) pDeviceHandler;
    BOOL        bMultiBand = FALSE;
    unsigned int        ii;

    for(ii=1;ii<=CB_MAX_CHANNEL;ii++) {
        sChannelTbl[ii].bValid = FALSE;
    }

    switch (pDevice->byRFType) {
        case RF_AL2230:
        case RF_AL2230S:
        case RF_VT3226:
        case RF_VT3226D0:
            bMultiBand = FALSE;
            break;
        case RF_AIROHA7230:
        case RF_VT3342A0:
        default :
            bMultiBand = TRUE;
            break;
    }

    if ((pDevice->dwDiagRefCount != 0) ||
        (pDevice->b11hEable == TRUE)) {
        if (bMultiBand == TRUE) {
            for(ii=0;ii<CB_MAX_CHANNEL;ii++) {
                sChannelTbl[ii+1].bValid = TRUE;
                //pDevice->abyRegPwr[ii+1] = pDevice->abyOFDMDefaultPwr[ii+1];
                //pDevice->abyLocalPwr[ii+1] = pDevice->abyOFDMDefaultPwr[ii+1];
            }
            for(ii=0;ii<CB_MAX_CHANNEL_24G;ii++) {
                //pDevice->abyRegPwr[ii+1] = pDevice->abyCCKDefaultPwr[ii+1];
                //pDevice->abyLocalPwr[ii+1] = pDevice->abyCCKDefaultPwr[ii+1];
            }
        } else {
            for(ii=0;ii<CB_MAX_CHANNEL_24G;ii++) {
                sChannelTbl[ii+1].bValid = TRUE;
                //pDevice->abyRegPwr[ii+1] = pDevice->abyCCKDefaultPwr[ii+1];
                //pDevice->abyLocalPwr[ii+1] = pDevice->abyCCKDefaultPwr[ii+1];
            }
        }
    } else if (pDevice->byZoneType <= CCODE_MAX) {
        if (bMultiBand == TRUE) {
            for(ii=0;ii<CB_MAX_CHANNEL;ii++) {
                if (ChannelRuleTab[pDevice->byZoneType].bChannelIdxList[ii] != 0) {
                    sChannelTbl[ii+1].bValid = TRUE;
                    //pDevice->abyRegPwr[ii+1] = ChannelRuleTab[pDevice->byZoneType].byPower[ii];
                    //pDevice->abyLocalPwr[ii+1] = ChannelRuleTab[pDevice->byZoneType].byPower[ii];
                }
            }
        } else {
            for(ii=0;ii<CB_MAX_CHANNEL_24G;ii++) {
                if (ChannelRuleTab[pDevice->byZoneType].bChannelIdxList[ii] != 0) {
                    sChannelTbl[ii+1].bValid = TRUE;
                    //pDevice->abyRegPwr[ii+1] = ChannelRuleTab[pDevice->byZoneType].byPower[ii];
                    //pDevice->abyLocalPwr[ii+1] = ChannelRuleTab[pDevice->byZoneType].byPower[ii];
                }
            }
        }
    }
    DBG_PRT(MSG_LEVEL_NOTICE, KERN_INFO"Zone=[%d][%c][%c]!!\n",pDevice->byZoneType,ChannelRuleTab[pDevice->byZoneType].chCountryCode[0],ChannelRuleTab[pDevice->byZoneType].chCountryCode[1]);
    for(ii=0;ii<CB_MAX_CHANNEL;ii++) {
        DBG_PRT(MSG_LEVEL_DEBUG, KERN_INFO"Channel[%d] is [%d]\n",sChannelTbl[ii].byChannelNumber,sChannelTbl[ii+1].bValid);
        /*if (pDevice->abyRegPwr[ii+1] == 0) {
            pDevice->abyRegPwr[ii+1] = pDevice->abyOFDMDefaultPwr[ii+1];
        }
        if (pDevice->abyLocalPwr[ii+1] == 0) {
            pDevice->abyLocalPwr[ii+1] = pDevice->abyOFDMDefaultPwr[ii+1];
        }*/
    }
}

BYTE CHbyGetChannelMapping(BYTE byChannelNumber)
{
BYTE    ii;
BYTE    byCHMapping = 0;

    for (ii=1; ii<=CB_MAX_CHANNEL; ii++ ) {
        if ( sChannelTbl[ii].byChannelNumber == byChannelNumber ) {
            byCHMapping = ii;
        }
    }
    return byCHMapping;
}
